var countTo = angular.module('countTo', [])
    .directive('countTo', ['$timeout', '$filter', function ($timeout, $filter) {
        return {
            replace: false,
            scope: {
              rawCountTo: '=countTo',
              rawDuration: '=duration',
              rawValue: '=value',
              refreshInterval: '='
            },
            link: function (scope, element, attrs) {

                var e = element[0];
                var num, refreshInterval, duration, steps, step, countTo, value, increment;

                var calculate = function () {
                    refreshInterval = parseInt(scope.refreshInterval) || 30;
                    step = 0;
                    scope.timoutId = null;
                    countTo = parseInt(scope.rawCountTo) || 0;
                    scope.value = parseInt(scope.rawValue, 10) || 0;
                    duration = (parseFloat(scope.rawDuration) * 1000) || 0;
                    steps = Math.ceil(duration / refreshInterval);
                    increment = ((countTo - scope.value) / steps);
                    num = scope.value;
                }

                var tick = function () {
                    scope.timoutId = $timeout(function () {
                        num += increment;
                        step++;
                        if (step >= steps) {
                            $timeout.cancel(scope.timoutId);
                            num = countTo;
                            e.innerHTML = $filter('counter')(countTo);
                        } else {
                            e.innerHTML = $filter('counter')(Math.round(num));
                            tick();
                        }
                    }, refreshInterval);

                }

                var start = function () {
                    if (scope.timoutId) {
                        $timeout.cancel(scope.timoutId);
                    }
                    calculate();
                    tick();
                }

                scope.$watch('rawCountTo', function (val) {
                    if (val) {
                        start();
                    }
                });

                /*attrs.$observe('value', function (val) {
                    start();
                });*/

                return true;
            }
        }

    }]);